import React from "react"

class User extends React.Component {  //компонент
   render(){
    return(
        <div className="user" key={el.id}>
        <h3>{el.firstname} {el.lastname}</h3>
        <p>{el.bio}</p>
        <b>{el.isHappy ? 'Счастлив :)' : 'Не особо :('}</b>
    </div>
    )
   }
}

export default User
